	public int tableActions(Rectangle r) {
		
		for(int i = 0; i < table.length; i++)
			for(int j = 0; j < table[i].length; j++) {

				if(table[i][j] == 1) {
					
					Rectangle obstacle = new Rectangle(j * tile, i * tile, tile, tile);
					if(r.intersects(obstacle)) return 1;
				}
			}
		return 0;
	}
			
